import Layout from '../components/Layout';
import {showSuccessMessage, showErrorMessage} from '../utils/alert';
import {useState} from 'react';
import axios from 'axios';
import {API} from '../config';


const Registration = () =>  {
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        error:'',
        success:'',
        buttonText:'Submit'
    })

    const {name, email, password , error, success, buttonText} = state
  
    const registrationHead = () => {
        return (
            <React.Fragment>
                <h1>Register</h1>
                <br />
            </React.Fragment>
        )
    }

    const handleChange = (attributeName) => (e) => {
        setState({...state, [attributeName]: e.target.value, error:'', success:'', buttonText:'Submit'})
    }

    const handleSubmit =  async e => {
        e.preventDefault();

        setState({...state, buttonText: 'Registering...' })
        try {

            //console.table({name, email, password})
            console.log('url', `${API}/register`)
            const response = await axios.post(`${API}/register`,{
                name, email, password
            })

            console.log('response: ' , response);
 
             //if success , empty all field
             setState({...state,
                name:'',
                email:'',
                password:'',
                buttonText: 'Submitted',
                success: response.data.message
            });

        }catch(error) {
            console.log('error: ' , error);
            setState({...state,
                buttonText: 'Register',
                error: error.response.data.error
            });
        }
    }

    /*
    const handleSubmit =  e => {
        e.preventDefault(); 

        setState({...state, buttonText: 'Registering...' })
        
        //console.table({name, email, password})
        axios.post(`http://localhost:8009/api/register`,{
            name, email, password
        })
        .then(res => {
 
            //if success , empty all field
            setState({...state,
                name:'',
                email:'',
                password:'',
                buttonText: 'Submitted',
                success: res.data.message
            });
         })
        .catch(err => {
            console.log('error: ' , err);
            setState({...state,
                buttonText: 'Register',
                error: err.response.data.message
            });
         })
    } 
*/

    const registrationForm = () => {
        return (
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input 
                        value={name}
                        onChange={handleChange('name')} 
                        type="text" className="form-control" placeholder="Type your name" />
                </div>
                <div className="form-group">
                    <input 
                        value={state.email}
                        onChange={handleChange('email')} 
                        type="email" className="form-control" placeholder="Type your email" />
                </div>
                <div className="form-group">
                    <input 
                        value={state.password}
                        onChange={handleChange('password')} 
                        type="password" className="form-control" placeholder="Type your password" />
                </div>
                <div className="form-group">
                    <button className="btn btn-outline-warning" > {state.buttonText} </button>
                </div>
            </form>
        )
    }

    const registrationMessage = () => {
        return (
            <React.Fragment>
                {success && showSuccessMessage(success)}
                {error && showErrorMessage(error)}
            </React.Fragment>
        )
    }

    return (
        <Layout>
            
            <div className="col-md-6 offset-md-3">
                {registrationHead()}
                {registrationMessage()}
                {registrationForm()}
                <hr/>
                {JSON.stringify(state)}
            </div>
        </Layout>
    )
}
export default Registration
