 import Link from 'next/link'
 import Router from 'next/router'

 import Layout from '../../../components/Layout';
 import {showSuccessMessage, showErrorMessage} from '../../../utils/alert';
 import {useEffect, useState} from 'react';
 import axios from 'axios';
 import {API} from '../../../config';
 import { authenticate, isAuth } from '../../../utils/auth';
 import {API_Login} from '../../../listAPI'
 import {Page_User, Page_Admin} from '../../../listPage'

const Login = () =>  {
     
    //if user has login, can't display registration screen and will be redirected to home
    useEffect(() => {
        isAuth() && Router.push(Page_User);
    }, []);
     

    //list of button state
    const buttonState = ['Login', 'Logging in..'];

    //set initial state
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        error:'',
        success:'',
        buttonText: buttonState[0]
    })

    const {name, email, password , error, success, buttonText} = state
  
    //set head
    const loginHead = () => {
        return (
            <React.Fragment>
                <h1>Login</h1>
                <br />
            </React.Fragment>
        ) 
    }

    const handleChange = (attributeName) => (e) => {
        setState({...state, [attributeName]: e.target.value, error:'', success:'', buttonText: buttonState[0]})
    }

    const handleSubmit =  async e => {
        e.preventDefault();

        setState({...state, buttonText: buttonState[1] })
        try {

            //console.table({name, email, password})
            const response = await axios.post(`${API}/${API_Login}`,{
                email, password
            })

            //authenticate
            authenticate(response, () => {
                 return isAuth() && isAuth().role === 'admin' ? Router.push(Page_Admin) : Router.push(Page_User) // redirect to home
            })
  
        }catch(error) {
            console.log('error: ' , error);
            setState({...state,
                buttonText:  buttonState[0],
                error: error.response.data.error
            });
        }
    }
 
    //set form
    const loginForm = () => {
        return (
            <form onSubmit={handleSubmit}>
                 
                <div className="form-group">
                    <input 
                        value={state.email}
                        onChange={handleChange('email')} 
                        type="email" className="form-control" placeholder="Type your email" 
                        required
                        />
                </div>
                <div className="form-group">
                    <input 
                        value={state.password}
                        onChange={handleChange('password')} 
                        type="password" className="form-control" placeholder="Type your password" 
                        required
                        />
                </div>
                <div className="form-group">
                    <button className="btn btn-outline-warning" > {state.buttonText} </button>
                </div>
            </form>
        )
    }

    //set message
    const loginMessage = () => {
        return (
            <React.Fragment>
                {success && showSuccessMessage(success)}
                {error && showErrorMessage(error)}
            </React.Fragment>
        )
    }

    //return page layout
    return (
        <Layout>
            <div className="col-md-6 offset-md-3">
                {loginHead()}
                {loginMessage()}
                {loginForm()}
                <hr/>
             </div> 
        </Layout>
    )
}
  
export default Login
