import { withRouter } from 'next/router';
import {useState, useEffect} from 'react';
import jwt from 'jsonwebtoken'
import axios from 'axios'
import {showSuccessMessage, showErrorMessage} from '../../../utils/alert';
import {API} from '../../../config';
import Layout from '../../../components/Layout';

const ActivateAccount = ({router}) => {

    const accountState = ['Activate Account', 'Activating...', 'Account is activated']

    const [state, setState] = useState({
        name:'',
        token:'',
        buttonText: accountState[0],
        success: '',
        error: ''
    });

    const {name, token, buttonText, success, error } = state;
 
    useEffect(() => {
        let token = router.query.id;

        if(token) {
            const {name} = jwt.decode(token)
            setState({...state, name, token})
        }
    },[router])
    
    const clickSubmit = async e => {
        e.preventDefault();

        setState({...state, buttonText: accountState[1]})

        try {

        }
        catch(error) {
            
        }
        console.log('Account is activated')
    }

    const message = () => {
        return (
            <React.Fragment>
                {success && showSuccessMessage(success)}
                {error && showErrorMessage(error)}
            </React.Fragment>
        )
    }
 
    //

    return <Layout>
        <div className="row">
            <div className="col-md-6 offset-md-3">
                <h1>Activate your email</h1>
                <br/>
                
                {message()}
                <button className="btn btn-outline-warning btn-block" onClick={clickSubmit}>{buttonText}</button>
            </div>
        </div>

    </Layout>
};

export default withRouter(ActivateAccount);
