import { withRouter } from 'next/router';
import {useState, useEffect} from 'react';
import jwt from 'jsonwebtoken'
import axios from 'axios'
import {showSuccessMessage, showErrorMessage} from '../../../../utils/alert';
import {API} from '../../../../config';
import Layout from '../../../../components/Layout';

import { listAPI } from '../../../../listAPI';

const ResetPassword = ({router}) => {

    const accountState = ['Submit', 'Submitting...']

    const [state, setState] = useState({
        newPassword:'',
        token:'',
        buttonText: accountState[0],
        success: '',
        error: ''
    });

    const {newPassword, token, buttonText, success, error } = state;
 
    useEffect(() => {
        let token = router.query.id;

        if(token) {
            const {name} = jwt.decode(token)
            setState({...state, name, token})
        }
    },[router])
    
    const handleChange = (attributeName) => (e) => {
        setState({...state, [attributeName]: e.target.value, error:'', success:'', buttonText:buttonState[0]})
    } 

    const handleSubmit = async e => {
        e.preventDefault();

        setState({...state, buttonText: accountState[1]})

        try {
            const response = await axios.post(`${API}${listAPI.API_ForgetPassword}`, { resetPasswordLink: token, newPassword });
 
            setState({...state, newPassword:'', token:'', buttonText: accountState[0],
                success: response.data.message,
                error: response.data.error,
            })
        }
        catch(error) {
            setState({...state, newPassword:'', token:'', buttonText: accountState[0], error: error.response.data.error})
        }

    }

    
    const passwordResetHead = () => {
        return (
            <React.Fragment>
                <h1>Submit your new password</h1>
                <br/>
            </React.Fragment>
        )
    }

    const passwordResetMessage = () => {
        return (
            <React.Fragment>
                {success && showSuccessMessage(success)}
                {error && showErrorMessage(error)}
            </React.Fragment>
        )
    }

    const passwordResetForm = () => {
        return (
            <React.Fragment>
                
                <div className="form-group">
                    <input 
                        value={state.newPassword}
                        onChange={handleChange('newPassword')} 
                        type="email" className="form-control" placeholder="Type your new pssword" 
                        required
                        />
                </div>
                <button className="btn btn-outline-warning btn-block" onClick={handleSubmit}>{buttonText}</button>
            </React.Fragment>
        )
    } 

    return <Layout>
        <div className="row">
            <div className="col-md-6 offset-md-3">
                
                {passwordResetHead()}
                {passwordResetMessage()}
                {passwordResetForm()}

            </div>
        </div>

    </Layout>
};

export default withRouter(ResetPassword);
