import cookie from 'js-cookie'

const keyToken = 'token';
const keyUser = 'user';

//set in cookie
export const setCookie = (key,value) => {
    if(process.browser) {
        cookie.set(key, value, {
            expires: 1 // cookie will be expired in 1 day
        })
    }
}

//remove from cookie
export const removeCookie = (key) => {
    if(process.browser) {
        cookie.set(key)
    }
}

//get data from cookie, such as: token. It will be useful when we need to make request to server with auth token
export const getCookie = (key) => {
    if(process.browser) {
        return cookie.get(key)
    }
}

//set in localstorage
export const setLocalStorage = (key, value) => {
    if(process.browser) {
        localStorage.setItem(key, JSON.stringify(value))
    }
}

//remove from localstorage
export const removeLocalStorage = (key) => {
    if(process.browser) {
        localStorage.removeItem(key)
    }
}

//authenticate 
export const authenticate = (response, next) => {
    setCookie(keyToken, response.data.token);
    setLocalStorage(keyUser, response.data.user);
    next();
}

//access user info from local storage
export const isAuth = () => {
    if(process.browser) {
        const cookieChecked = getCookie(keyToken)
        if (cookieChecked) {
            if(localStorage.getItem(keyUser)) {
                return JSON.parse(localStorage.getItem(keyUser));
            } else {
                return false;
            }
        }
    }
}
