import Layout from '../components/Layout'
import {useState} from 'react'

const Registration = () =>  {
    const [state, setState] = useState({
        name: '',
        email: '',
        password: '',
        error:'',
        success:'',
        buttonText:'Submit'
    })

    const {name, email, password , error, success, buttonText} = state
  
    const registrationHead = () => {
        return (
            <React.Fragment>
                <h1>Register</h1>
                <br />
            </React.Fragment>
        )
    }

    const handleChange = (attributeName) => (e) => {
        setState({...state, [attributeName]: e.target.value, error:'', success:'', buttonText:'Submit'})
    }

    const handleSubmit = (e) => {
        e.preventDefault();
        
        //console.table({name, email, password})
        axios.post(`http://localhost:8009/api/register`,{
            name, email, password
        })
    } 

    const registrationForm = () => {
        return (
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <input 
                        value={name}
                        onChange={handleChange('name')} 
                        type="text" className="form-control" placeholder="Type your name" />
                </div>
                <div className="form-group">
                    <input 
                        value={state.email}
                        onChange={handleChange('email')} 
                        type="email" className="form-control" placeholder="Type your email" />
                </div>
                <div className="form-group">
                    <input 
                        value={state.password}
                        onChange={handleChange('password')} 
                        type="password" className="form-control" placeholder="Type your password" />
                </div>
                <div className="form-group">
                    <button className="btn btn-outline-warning" > {state.buttonText} </button>
                </div>
            </form>
        )
    }

    return (
        <Layout>
            <div className="col-md-6 offset-md-3">
                {registrationHead()}
                {registrationForm()}
                <hr/>
                {JSON.stringify(state)}
            </div>
        </Layout>
    )
}
export default Registration
